import numpy as np

def score(weight, value, remaining_capacity):
    if weight > remaining_capacity:
        return -1e9
    # {The algorithm scores each item based on the ratio of its value to the exponential of its weight, adjusted by the remaining capacity, ensuring that items with higher value and lower weight are prioritized.}
    score = (value / np.exp(weight)) * remaining_capacity
    return score
