import numpy as np

def place_facilities(peaks, weights, k):
    # {Implementing a 'Best Percentile Rule' by placing facilities at equally spaced quantiles.}

    sorted_peaks = np.sort(peaks)
    facilities_positions = []

    for i in range(k):
        frac = (i + 1) / (k + 1)
        idx = int(frac * (len(peaks) - 1))
        facilities_positions.append(sorted_peaks[idx])

    return facilities_positions
