import numpy as np

def calculate_modified_score(value, weight, remaining_capacity):
    if weight > remaining_capacity:
        return -1e9
    normalized_value = value / (weight + 1e-9)
    capacity_factor = (remaining_capacity - weight + 1) ** 0.5
    score = normalized_value * capacity_factor + np.log(value + 1)
    return float(score)

def score(weight, value, remaining_capacity):
    score = calculate_modified_score(value, weight, remaining_capacity)
    return score
