import numpy as np

def logistic(x):
    return 1 / (1 + np.exp(-x))

def score(weight, value, remaining_capacity):
    if weight > remaining_capacity:
        return -1e9
    value_to_weight_ratio = value / weight
    weight_ratio = weight / remaining_capacity
    logistic_factor = logistic(weight_ratio)
    score = value_to_weight_ratio * logistic_factor
    return score
